import React, { useState } from 'react';

const SearchAnime = () => {
  const [query, setQuery] = useState('');
  const [animeList, setAnimeList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    if (!query) return;

    setLoading(true);
    setError('');
    setAnimeList([]);

    try {
      const response = await fetch(`http://localhost:8000/anime/search/?query=${query}`);
      const data = await response.json();

      if (response.ok) {
        if (data.length === 0) {
          setError('No se encontraron animes.');
        } else {
          setAnimeList(data);
        }
      } else {
        setError(data.error || 'Ocurrió un error.');
      }
    } catch (err) {
      setError('No se pudo conectar con el servidor.');
    }

    setLoading(false);
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>Buscar Anime</h1>
      <input
        type="text"
        placeholder="Escribe el nombre de un anime"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ padding: '0.5rem', width: '300px', marginRight: '10px' }}
      />
      <button onClick={handleSearch} style={{ padding: '0.5rem 1rem' }}>Buscar</button>

      {loading && <p>Cargando...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ul style={{ listStyle: 'none', padding: 0, marginTop: '1rem' }}>
        {animeList.map((anime, index) => (
          <li key={index} style={{ marginBottom: '10px' }}>
            <a href={`https://www3.animeflv.net${anime.link}`} target="_blank" rel="noreferrer">
              {anime.title}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchAnime;
