import React, { useState } from "react";

const AnimeSearch = () => {
  const [searchQuery, setSearchQuery] = useState("");  // Para el nombre del anime
  const [animeList, setAnimeList] = useState([]);      // Para la lista de resultados
  const [loading, setLoading] = useState(false);       // Estado de carga

  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Por favor ingresa un nombre de anime.");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/anime/search/${searchQuery}`);
      const data = await response.json();

      if (response.ok) {
        setAnimeList(data);  // Ahora los animes incluirán episodios
      } else {
        alert(data.error || "No se encontraron animes.");
      }
    } catch (error) {
      console.error("Error al buscar el anime:", error);
      alert("Hubo un problema al buscar el anime.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Escribe el nombre del anime..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Buscar"}
      </button>

      {animeList.length > 0 && (
        <div>
          <h3>Resultados:</h3>
          <ul>
            {animeList.map((anime, index) => (
              <li key={index}>
                <h4>
                  <a href={anime.link} target="_blank" rel="noopener noreferrer">
                    {anime.name}
                  </a>
                </h4>
                <ul>
                  {anime.episodes && anime.episodes.map((episode, i) => (
                    <li key={i}>
                      <a href={episode.episode_link} target="_blank" rel="noopener noreferrer">
                        {episode.episode_number} - {episode.episode_name}
                      </a>
                    </li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AnimeSearch;
