import React, { useState, useEffect } from "react";

const AnimeSearch = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = () => {
    // Verificar si hay un término de búsqueda
    if (searchQuery.trim() === "") {
      alert("Por favor ingresa un anime para buscar.");
      return;
    }

    // Redirigir a AnimeFLV con el término de búsqueda
    const animeFLVSearchUrl = `https://www3.animeflv.net/anime/${searchQuery}`;
    window.location.href = animeFLVSearchUrl;  // Redirigir a la URL
  };

  return (
    <div>
      <h1></h1>
      <input
        type="text"
        placeholder="Buscar anime..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Buscar</button>
    </div>
  );
};

export default AnimeSearch;
