const handleSearch = async () => {
  if (searchQuery.trim() === "") {
    alert("Por favor ingresa un nombre de anime.");
    return;
  }

  setLoading(true);
  try {
    const response = await fetch(`http://localhost:8000/api/anime/search/${searchQuery}`);
    const data = await response.json();

    if (response.ok) {
      console.log("Data recibida:", data);
      setAnimeList(data);  // ðŸ‘ˆ AsegÃºrate de guardar la lista directamente
    } else {
      alert(data.error || "No se encontraron animes.");
    }
  } catch (error) {
    console.error("Error al buscar el anime:", error);
    alert("Hubo un problema al buscar el anime.");
  } finally {
    setLoading(false);
  }
};
