import React, { useState } from "react";

const SearchAnime = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [animeList, setAnimeList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Por favor ingresa un nombre de anime.");
      return;
    }

    setLoading(true);
    setError("");  // Limpiar cualquier error previo

    try {
      // Realizar la petición a Django backend
      const response = await fetch(`http://127.0.0.1:8000/api/anime/search/${searchQuery}/`);

      // Verificar si la respuesta fue exitosa
      if (response.ok) {
        const data = await response.json();
        setAnimeList(data);  // Mostrar los resultados de los animes
      } else {
        const data = await response.json();
        setError(data.error || "No se encontraron resultados.");
        setAnimeList([]); // Limpiar resultados previos
      }
    } catch (error) {
      console.error("Error al buscar el anime:", error);
      setError("Hubo un problema al buscar el anime.");
      setAnimeList([]); // Limpiar resultados previos
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Buscar Anime</h2>
      <input
        type="text"
        placeholder="Ej: naruto"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Buscar"}
      </button>

      {/* Mostrar error si ocurre */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Mostrar los resultados */}
      {animeList.length > 0 && (
        <div>
          <h3>Resultados:</h3>
          {animeList.map((anime, index) => (
            <div key={index} style={{ marginBottom: "20px" }}>
              <h4>{anime.name}</h4>
              <a href={anime.link} target="_blank" rel="noopener noreferrer">
                Ver en AnimeFLV
              </a>
              {anime.episodes && anime.episodes.length > 0 && (
                <ul>
                  {anime.episodes.map((ep, idx) => (
                    <li key={idx}>
                      <strong>Cap {ep.episode_number}:</strong>{" "}
                      <a href={ep.episode_link} target="_blank" rel="noopener noreferrer">
                        {ep.episode_name}
                      </a>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchAnime;
