import React, { useState } from "react";

const SearchAnime = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [animeList, setAnimeList] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/anime/search/${searchQuery}`);
      const data = await response.json();
      setAnimeList(data);
    } catch (error) {
      console.error("Error:", error);
      alert("Error al buscar anime.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <input
        type="text"
        placeholder="Ej: naruto"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Buscar"}
      </button>

      {animeList.length > 0 && (
        <div>
          <h2>Resultados:</h2>
          {animeList.map((anime, index) => (
            <div key={index} style={{ marginBottom: "20px" }}>
              <h3>{anime.name}</h3>
              <a href={anime.link} target="_blank" rel="noopener noreferrer">Ver en AnimeFLV</a>
              <ul>
                {anime.episodes.map((ep, idx) => (
                  <li key={idx}>
                    <strong>Cap {ep.episode_number}:</strong> {ep.episode_name} â€”
                    <a href={ep.episode_link} target="_blank" rel="noopener noreferrer"> Ver episodio</a>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchAnime;
