import React, { useState } from "react";

const SearchAnime = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [animeList, setAnimeList] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Por favor ingresa un nombre de anime.");
      return;
    }

    setLoading(true);

    try {
      // Asegúrate de que la URL esté correcta para hacer la solicitud al backend de Django
      const response = await fetch(`http://localhost:8000/api/anime/search/${searchQuery}`);
      const data = await response.json();

      if (Array.isArray(data)) {
        setAnimeList(data);  // Si los datos son una lista de animes, los mostramos
      } else {
        alert(data.error || "No se encontraron resultados.");
        setAnimeList([]);  // Si no se encontraron resultados, vaciar la lista
      }
    } catch (error) {
      console.error("Error al buscar el anime:", error);
      alert("Hubo un problema al buscar el anime.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Introduce el nombre del anime"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Buscar"}
      </button>

      {animeList.length > 0 && (
        <div>
          <h3>Resultados:</h3>
          {animeList.map((anime, index) => (
            <div key={index} style={{ marginBottom: "20px" }}>
              <h4>{anime.name}</h4>
              <a href={anime.link} target="_blank" rel="noopener noreferrer">
                Ver en AnimeFLV
              </a>
              {anime.episodes && anime.episodes.length > 0 && (
                <ul>
                  {anime.episodes.map((ep, idx) => (
                    <li key={idx}>
                      <strong>Capítulo {ep.episode_number}:</strong>{" "}
                      <a href={ep.episode_link} target="_blank" rel="noopener noreferrer">
                        {ep.episode_name}
                      </a>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchAnime;
