import React, { useState } from "react";

const AnimeSearch = () => {
  const [searchQuery, setSearchQuery] = useState("");  // Para el nombre del anime
  const [animeList, setAnimeList] = useState([]);      // Para la lista de resultados
  const [loading, setLoading] = useState(false);       // Estado de carga

  // Función que maneja la búsqueda de anime
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Por favor ingresa un nombre de anime.");
      return;
    }

    setLoading(true);
    try {
      // Realiza la solicitud al backend
      const response = await fetch(`/api/anime/search/${searchQuery}`);
      const data = await response.json();

      console.log(data);  // Verifica lo que recibes en la consola

      if (response.ok) {
        setAnimeList(data.animes || []);  // Asegúrate de que animeList siempre sea un array
      } else {
        alert(data.error || "No se encontraron animes.");
      }
    } catch (error) {
      console.error("Error al buscar el anime:", error);
      alert("Hubo un problema al buscar el anime.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Escribe el nombre del anime..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}  // Actualiza el estado con la búsqueda
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Buscar"}
      </button>

      {animeList.length > 0 && (
        <div>
          <h3>Resultados:</h3>
          <ul>
            {animeList.map((anime, index) => (
              <li key={index}>
                <a href={anime.link} target="_blank" rel="noopener noreferrer">
                  {anime.name}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AnimeSearch;
